def on_b_repeated():
    game.reset()
controller.B.on_event(ControllerButtonEvent.REPEATED, on_b_repeated)

def on_on_overlap(sprite, otherSprite):
    global count
    info.change_life_by(-1)
    count = 0
    otherSprite.destroy(effects.bubbles, 200)
    scene.camera_shake(6, 100)
sprites.on_overlap(SpriteKind.player, SpriteKind.projectile, on_on_overlap)

def on_on_destroyed(sprite):
    global count, level
    count += 0.2
    level += 1
    info.change_score_by(1)
sprites.on_destroyed(SpriteKind.projectile, on_on_destroyed)

