def on_on_update():
    global count, speed
    if count >= 7:
        info.change_life_by(1)
        count = 0
    if level == 100:
        mySprite2.destroy()
        game.splash("第二關")
        speed = randint(60, 80)
    elif level == 200:
        mySprite2.destroy()
        game.splash("第三關")
        speed = randint(80, 100)
    elif level == 300:
        music.pew_pew.play()
        mySprite2.destroy()
        game.splash("遊戲結束")
        game.reset()
    print(level)
    print(mySprite2)
game.on_update(on_on_update)

def on_update_interval():
    global mySprite2
    mySprite2 = sprites.create(img("""
            . . b b b b . . 
                    . b 5 5 5 5 b . 
                    b 5 d 3 3 d 5 b 
                    b 5 3 5 5 1 5 b 
                    c 5 3 5 5 1 d c 
                    c d d 1 1 d d c 
                    . f d d d d f . 
                    . . f f f f . .
        """),
        SpriteKind.projectile)
    mySprite2.set_flag(SpriteFlag.AUTO_DESTROY, True)
    mySprite2.set_position(randint(10, 150), 0)
    mySprite2.vy = speed
game.on_update_interval(200, on_update_interval)

